+						Por favor completar los detalles para {typeText} la cita
+					</DialogDescription>
+				</DialogHeader>
+
+				<AppointmentForm
+					userId={userId}
+					patientId={patientId}
+					type={type}
+					appointment={appointment}
+                    setOpen={setOpen}
+				/>
+			</DialogContent>
+		</Dialog>
+	);
+};

